<?php include("fijo/controlsesion.php"); ?><?php //Conectamos con la DBinclude("fijo/datosaccesodb.php");$conexion = mysqli_connect($host, $usuario, $clave, $db);if (mysqli_connect_errno()) {	echo "Fallo al intentar conectar con la base de datos: (" . mysqli_connect_errno() . ")";	die();}mysqli_query($conexion, "SET NAMES 'utf8'");//Creamos el registro en la tabla para lugo obtener el id$sql = "INSERT INTO sponsor (nombre, imagen) VALUES ('".$_POST['nombresponsor']."', 'pendiente')";if (!mysqli_query($conexion, $sql)) {	echo "No se pudo crear el lugar: (" . mysqli_connect_errno() . ")";	die();}$ruta="../img/sponsors/";//Como no sabemos cuantos archivos van a llegar, iteramos la variable $_FILESforeach ($_FILES as $imagen) {	if($imagen['error'] == UPLOAD_ERR_OK ){//Verificamos si se subio correctamente	  $pathinfo = pathinfo($imagen['name']);//Desglosamos el nombre del archivo (en nombre y extension)	  $ext = strtolower($pathinfo['extension']);//Obtenemos la extension del archivo original (en minuscula)	  $ext = ($ext == '') ? $ext : '.' . $ext;//Si tiene extensión le agregamos un punto delante	  if($ext != ".jpeg" AND $ext != ".jpg" AND $ext != ".png" AND $ext != ".gif"){		$sql = "DELETE FROM sponsor WHERE  id=".mysqli_insert_id($conexion);		mysqli_query($conexion, $sql);		echo "El archivo subido solo puede ser jpeg, jpg, png o gif";		die();	  }	  $nombre = mysqli_insert_id($conexion);//El nombre del archivo será el ID del lugar	  $temporal = $imagen['tmp_name']; //Obtenemos el nombre del archivo temporal	  move_uploaded_file($temporal, $ruta . $nombre . $ext); //Movemos el archivo temporal a la ruta especificada	  //Actualizamos el registro creado para colocar correctamente el nombre de la imagen subida.	  $sql = "UPDATE sponsor SET imagen='".$nombre.$ext."' WHERE  id=".mysqli_insert_id($conexion);	  if (!mysqli_query($conexion, $sql)) {		echo "No se pudo guardar el nombre de la imagen en la base de datos: (" . mysqli_connect_errno() . ")";		die();	  }	   echo 0;	}else{	  echo "No se pudo cargar imágen: ".$imagen['error']; //Si no se cargo mostramos el error	}}mysqli_close($conexion);?>